import React, { Component } from 'react'
import { Icon, Menu } from "antd"
import {Route,Switch,Redirect} from "react-router-dom"
import "../../assets/css/index/index.css"
import {indexRoutes} from "../../router/routes"
import Footer from "../public/footer"




export default class Index extends Component {
    state = {
        current: '',
    };
    shuaxin(){
        const reg=/^\/index\/(.{1,})$/g;
        const key =reg.exec(this.props.location.pathname)
        this.setState({
            current: key,
        });
    }
    handleClick = (e) => {
        this.setState({
            current: e.key,
        });
        this.props.history.push("/index/sport",{
            tagId:e.tagId
        })
        
    };
    // toList=(tagId)=>{
    //     this.props.history.push("/index/sport",{
    //         tagId
    //     })
    // }
    componentWillMount(){
        this.shuaxin()
    }
    render() {
        return (
            <div className="index">
                <div className="search">
                    <Icon type="search" />
                    <span>搜索商品</span>
                </div>
                <div className="nav">
                    <Menu onClick={this.handleClick} selectedKeys={[this.state.current]} mode="horizontal">
                        <Menu.Item key="one" tagId="">
                            <span  onClick={()=>{this.props.history.push("/index/one")}}>首页</span>
                        </Menu.Item>
                        <Menu.Item key="eat" tagId="108072253">
                            <span>寻味</span>
                        </Menu.Item>
                        <Menu.Item key="beautiful" tagId="108088088">
                            <span>美妆</span>
                        </Menu.Item>
                        <Menu.Item key="children" tagId="108088088">
                            <span>儿童</span>
                        </Menu.Item>
                        <Menu.Item key="home" tagId="108072001">
                            <span>居家</span>
                        </Menu.Item>
                        <Menu.Item key="out" tagId="108072009">
                            <span>户外</span>
                        </Menu.Item>
                        <Menu.Item key="direction" tagId="108071966">
                            <span>配饰</span>
                        </Menu.Item>
                    </Menu>
                </div>
                <div className="indexContent">
                    <Switch>
                        {
                            indexRoutes.map(item=>{
                                return <Route key={item.path} path={item.path} component={item.component} exact/>
                            })
                        }

                        <Redirect to="/index/one" from="/index" exact/>
                    </Switch>
                    
                </div>
                <Footer />
            </div>
        )
    }
}
