import React, { Component } from 'react'
import { Icon, Menu } from "antd"
import "../../assets/css/index/index.css"
import {indexRoutes} from "../../router/routes"
export default class Index extends Component {
    state = {
        current: 'one',
    };

    handleClick = e => {
        console.log('click ', e);
        this.setState({
            current: e.key,
        });
        this.props.history.push(`/index/${e.key}`)
        
    };
    render() {
        return (
            <div>
                <div className="search">
                    <Icon type="search" />
                    <span>搜索商品</span>
                </div>
                <div className="nav">
                    <Menu onClick={this.handleClick} selectedKeys={[this.state.current]} mode="horizontal">
                        <Menu.Item key="one">
                            首页
                        </Menu.Item>
                        <Menu.Item key="eat">
                            寻味
                        </Menu.Item>
                        <Menu.Item key="beautiful">
                            美妆
                        </Menu.Item>
                        <Menu.Item key="children">
                            儿童
                        </Menu.Item>
                        <Menu.Item key="home">
                            居家
                        </Menu.Item>
                        <Menu.Item key="out">
                            户外
                        </Menu.Item>
                        <Menu.Item key="direction">
                            配饰
                        </Menu.Item>
                    </Menu>
                </div>
                <div className="content">
                    {
                        indexRoutes.map(item=>{
                            return <Route path={item.path} component={item.component} exact/>
                        })
                    }
                </div>
            </div>
        )
    }
}
